        public bool SendEmail(string to, string title, string body)
        {
            try
            {
                string _sender = "feels@monow.cc";
                string _password = "SmptM0n0w!";

                var email = new MimeMessage();
                email.From.Add(MailboxAddress.Parse(_sender));
                email.To.Add(MailboxAddress.Parse(to));

                email.Subject = title;

                

                email.Body = new TextPart(MimeKit.Text.TextFormat.Html) { Text = $"{RecoverPassword(body)}" };

                using var smtp = new SmtpClient();
                smtp.Connect("smtp.titan.email", 465, true);
                smtp.Authenticate(_sender, _password);
                smtp.Send(email);
                smtp.Disconnect(true);

                return true;
            }
            catch (Exception ex)
            {
                Console.WriteLine(ex.Message);
                return false;
            }
        }


